import React from 'react';
import { KeyboardAvoidingView, Platform, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native';
import Task from './components/Task'

export default function App() {
  return (
    <View style={styles.container}>
      <View style={styles.tasksWrapper}>
          <Text style={styles.sectionTitle}>
            Tarefas de hoje
          </Text>

          <View style={styles.items}>
              {/* Where the tasks will go */}
              <Task text={'Tarefa 1'} />
              <Task text={'Tarefa 2'} />
              <Task text={'Tarefa 3'} />
          </View>
        </View>

              {/* Section to write the task */}
              <KeyboardAvoidingView behavior={Platform.OS === "android" ? "padding" : "height"} style={styles.writeTaskWrapper}>
                <TextInput style={styles.input} placeholder={'Escreva uma tarefa'}/>
                <TouchableOpacity>
                  <View style={styles.addWrapper}>
                    <Text style={styles.addText}>+</Text>
                  </View>
                </TouchableOpacity>
              </KeyboardAvoidingView>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#C4C4C4',
  },
  tasksWrappers: {
    paddingTop: 80,
    paddingHorizontal: 20,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  items: {
    marginTop: 30,
  },
  writeTaskWrapper: {
    position: 'absolute'
  },
  input: {

  },
  addWrapper: {

  },
  addText: {

  },
});
